from sparknlp_jsl.common import *
from sparknlp_jsl.annotator import MedicalSummarizer as A


class FinanceSummarizer(A):
    name = "FinanceSummarizer"

    @keyword_only
    def __init__(self, classname="com.johnsnowlabs.finance.sequence_generation.FinanceSummarizer", java_model=None):
        super(FinanceSummarizer, self).__init__(
            classname=classname,
            java_model=java_model
        )

        self._setDefault(
            task="summarize: "
        )

    @staticmethod
    def loadSavedModel(folder, spark_session, use_cache):
        """Loads a locally saved model.

        Parameters
        ----------
        folder : str
            Folder of the saved model
        spark_session : pyspark.sql.SparkSession
            The current SparkSession
        use_cache: bool
            The model uses caching to facilitate performance

        Returns
        -------
        FinanceSummarizer
            The restored model
        """
        from sparknlp_jsl.internal import _FinanceSummarizerLoader
        jModel = _FinanceSummarizerLoader(folder, spark_session._jsparkSession, use_cache)._java_obj
        return FinanceSummarizer(java_model=jModel)

    @staticmethod
    def pretrained(name="finsum_flant5_finetuned_sec10k", lang="en", remote_loc="finance/models"):
        """Downloads and loads a pretrained model.

        Parameters
        ----------
        name : str, optional
            Name of the pretrained model, by default "finsum_flant5_finetuned_sec10k"
        lang : str, optional
            Language of the pretrained model, by default "en"
        remote_loc : str, optional
            Optional remote address of the resource, by default None. Will use
            Spark NLPs repositories otherwise.

        Returns
        -------
        FinanceSummarizer
            The restored model
        """
        from sparknlp_jsl.pretrained import InternalResourceDownloader
        return InternalResourceDownloader.downloadModel(FinanceSummarizer, name, lang, remote_loc,
                                                        j_dwn='InternalsPythonResourceDownloader')