from sparknlp_jsl.common import *


class WhiteBlackListParams:
    """ It is a base class that contains filtering functions related to white and black lists.
    """

    blackList = Param(Params._dummy(), "blackList", "If defined, list of entities to ignore. The rest will be processed. Do not include IOB prefix on labels", typeConverter = TypeConverters.toListString)

    def setBlackList(self, value):
        """Sets If defined, list of entities to ignore. The rest will be processed. Do not include IOB prefix on labels

        Parameters
        ----------
        value : List[str]
            If defined, list of entities to ignore. The rest will be processed. Do not include IOB prefix on labels
        """
        return self._set(blackList = value)

    def setDenyList(self, value):
        """Sets If defined, list of entities to ignore. The rest will be processed. Do not include IOB prefix on labels

        Parameters
        ----------
        value : List[str]
            If defined, list of entities to ignore. The rest will be processed. Do not include IOB prefix on labels
        """
        return self._set(blackList = value)

    whiteList = Param(Params._dummy(), "whiteList", "If defined, list of entities to process. The rest will be ignored. Do not include IOB prefix on labels", typeConverter = TypeConverters.toListString)

    def setWhiteList(self, value):
        """Sets If defined, list of entities to process. The rest will be ignored. Do not include IOB prefix on labels

        Parameters
        ----------
        value : List[str]
            If defined, list of entities to process. The rest will be ignored. Do not include IOB prefix on labels
        """
        return self._set(whiteList = value)

    caseSensitive = Param(
        Params._dummy(),
        "caseSensitive",
        "Determines whether the definitions of the white listed entities are case sensitive or not.",
        typeConverter=TypeConverters.toBoolean
    )

    def setCaseSensitive(self, value):
        """Determines whether the definitions of the white listed and black listed entities are case sensitive or not.

        Parameters
        ----------
        value : bool
           Whether white listed and black listed entities are case sensitive or not. Default: True.
        """
        return self._set(caseSensitive=value)


