from sparknlp_jsl.common import *
from sparknlp_jsl.annotator import MedicalTextGenerator as A


class FinanceTextGenerator(A):

    name = "FinanceTextGenerator"

    @keyword_only
    def __init__(self, classname="com.johnsnowlabs.finance.sequence_generation.FinanceTextGenerator",
                 java_model=None):
        super(FinanceTextGenerator, self).__init__(
            classname=classname,
            java_model=java_model
        )

    @staticmethod
    def loadSavedModel(folder, spark_session, model_type):
        """Loads a locally saved model.

        Parameters
        ----------
        folder : str
            Folder of the saved model
        spark_session : pyspark.sql.SparkSession
            The current SparkSession
        model_type : str
            The type of the model
        Returns
        -------
        FinanceTextGenerator
            The restored model
        """
        from sparknlp_jsl.internal import _FinanceTextGeneratorLoader
        jModel = _FinanceTextGeneratorLoader(folder, spark_session._jsparkSession, model_type)._java_obj
        return FinanceTextGenerator(java_model=jModel)

    @staticmethod
    def pretrained(name="fingen_flant5_finetuned_sec10k", lang="en", remote_loc="finance/models"):
        """Downloads and loads a pretrained model.

        Parameters
        ----------
        name : str, optional
            Name of the pretrained model, by default "fingen_flant5_finetuned_sec10k"
        lang : str, optional
            Language of the pretrained model, by default "en"
        remote_loc : str, optional
            Optional remote address of the resource, by default None. Will use
            Spark NLPs repositories otherwise.

        Returns
        -------
        FinanceTextGenerator
            The restored model
        """
        from sparknlp_jsl.pretrained import InternalResourceDownloader
        return InternalResourceDownloader.downloadModel(FinanceTextGenerator, name, lang, remote_loc,
                                                        j_dwn='InternalsPythonResourceDownloader')
