from sparknlp_jsl.common import *
from sparknlp_jsl.annotator import MedicalQuestionAnswering as A


class LegalQuestionAnswering(A):

    name = "LegalQuestionAnswering"

    @keyword_only
    def __init__(self, classname="com.johnsnowlabs.legal.sequence_generation.LegalQuestionAnswering",
                 java_model=None):
        super(LegalQuestionAnswering, self).__init__(
            classname=classname,
            java_model=java_model
        )

    @staticmethod
    def loadSavedModel(folder, spark_session):
        """Loads a locally saved model.

        Parameters
        ----------
        folder : str
            Folder of the saved model
        spark_session : pyspark.sql.SparkSession
            The current SparkSession

        Returns
        -------
        LegalQuestionAnswering
            The restored model
        """
        from sparknlp_jsl.internal import _LegalQuestionAnsweringLoader
        jModel = _LegalQuestionAnsweringLoader(folder, spark_session._jsparkSession)._java_obj
        return LegalQuestionAnswering(java_model=jModel)

    @staticmethod
    def pretrained(name="biogpt_pubmed_qa", lang="en", remote_loc="legal/models"):
        """Downloads and loads a pretrained model.

        Parameters
        ----------
        name : str, optional
            Name of the pretrained model, by default "biogpt_pubmed_qa"
        lang : str, optional
            Language of the pretrained model, by default "en"
        remote_loc : str, optional
            Optional remote address of the resource, by default None. Will use
            Spark NLPs repositories otherwise.

        Returns
        -------
        LegalQuestionAnswering
            The restored model
        """
        from sparknlp_jsl.pretrained import InternalResourceDownloader
        return InternalResourceDownloader.downloadModel(LegalQuestionAnswering, name, lang, remote_loc,
                                                        j_dwn='InternalsPythonResourceDownloader')